import React from 'react'
import Moment from 'react-moment'
import 'moment-timezone'

class DateItem extends React.Component {
  state = {
    attire: '',
    dateType: ''
  }

  componentDidMount() {
    let foundAttire = this.props.clothing.filter(item => {
      return item.id === this.props.date.attire_id
    })

    let foundDate = this.props.clothing.filter(date => {
      return date.id === this.props.date.rendezvous_type_id
    })

    this.setState({
      attire: foundAttire.name,
      dateType: foundDate.name
    })
  }

  render(){
    console.log(this.state);
    return(
      <div className="dateShow">
      <Moment format="MM-DD-YYYY">
               {this.props.date.date}
           </Moment>
           &nbsp;
           <Moment format="LT" tz="UTC">
                    {this.props.date.time}
                </Moment><br/>
                <ul>
                <li>Date Type: </li>
                <li>Attire: </li>
                <li>Pick-Up Location: {this.props.date.location}</li>
                </ul>
      </div>
    )
  }
}

export default DateItem
