import React from 'react';
import './App.css';
import LoginAndSignup from './components/LoginAndSignUpPage'
import SignUp from './components/SignUpForm'
import Profile from './components/ProfilePage'
import CreateDate from './components/CreateDatePage'
import { Switch, Route} from 'react-router-dom';


const DATETYPES = 'http://localhost:3000/rendezvous_type'
const ATTIRE ='http://localhost:3000/attire'
class App extends React.Component {
  state = {
    clothing: [],
    dateTypes: []
  }

  componentDidMount(){
    fetch(DATETYPES)
    .then(resp => resp.json())
    .then(dates => {
    let allDates =  dates.map(date => date.name)
    this.setState({dateTypes: allDates})
    })

    fetch(ATTIRE)
    .then(resp => resp.json())
    .then(option =>{
      let allOpts = option.map(opt => opt.name)
      this.setState({clothing: allOpts})
    })
    console.log(this.state);
  }


  render(){
    return (
      <Switch>
      <Route exact path="/" component={LoginAndSignup}/>
      <Route path="/signup" component={SignUp} />
      <Route path="/profile" component={Profile} />
      <Route path="/newdate" component={CreateDate} />
      </Switch>
    );
  }
}

export default App;
